element.calculation:
(function calculatedFieldValue(current, g_form) {
	var TOTAL_WEEKS_PER_YEAR = 26;
	var annual_salary = parseInt(g_form.getValue('annual_salary'));
	var result = annual_salary / TOTAL_WEEKS_PER_YEAR;
	return result.toString();

})(current, g_form);

(function calculatedFieldValue(current) {
	var tax = 0.90;
	var gross_income_per_paycheck = parseInt(g_form.getValue('gross_income_per_paycheck'));
	var result = annual_salary * tax;
	return result.toString();

})(current);

(function calculatedFieldValue(current) {
	var TOTAL_WEEKS_OF_YEAR = 56;
	var current_week_of_year = 20; //just an example

	var annual_salary = parseInt(g_form.getValue('annual_salary'));
	var result = (current_week_of_year * annual_salary) / TOTAL_WEEKS_OF_YEAR;
	return result.toString();

})(current);
