sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

    // Modified the if to return if the newValue == oldValue to avoid
    // unecessary trips to the server
    if (newValue === '' || newValue == oldValue) {
        return;
    }
	g_form.setVisible('external_project_name', false);
	g_form.setVisible('meeting_presentation_attendees', false);
	g_form.setVisible('meeting_presentation_date', false);
	g_form.setVisible('meeting_presentation_subject', false);

	var EXTERNAL_PROJECT = "1";
	var MEETING_PRESENTATION = "3";

	switch(newValue) {
		case EXTERNAL_PROJECT:
			g_form.setVisible('external_project_name', true);
			break;
		case MEETING_PRESENTATION:
			g_form.setVisible('meeting_presentation_attendees', true);
			g_form.setVisible('meeting_presentation_date', true);
			g_form.setVisible('meeting_presentation_subject', true);
			break;
   }
}
