sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var TOTAL_WEEKS_PER_YEAR = 26;
	var valueOnly =  1 * ((newValue.split(';'))[1].replaceAll(',', ''));
    var annual_salary = valueOnly;
    var gross_income_per_paycheck = annual_salary / TOTAL_WEEKS_PER_YEAR;
    g_form.setValue('gross_income_per_paycheck', 'USD;' + gross_income_per_paycheck);


    var paycheck_amount_after_taxes = annual_salary * 0.9;
    g_form.setValue('paycheck_amount_after_taxes', 'USD;' + paycheck_amount_after_taxes);

	var ytd_income = annual_salary * 0.9;
    g_form.setValue('ytd_income', 'USD;' + ytd_income);
}
