sys_script_include.script:
var EmployeeInformation = Class.create();
// Extend the global.AbstractAjaxProcessor class
EmployeeInformation.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    // Define the getEmail function.  
    // Create a GlideRecord for the User table.
    // Use the sysparm_userID passed from the client side to retrieve a record from the User table.
    // Return the email address for the requested record
    getEmployeePosition: function() {
        var employeeRecord = new GlideRecord("x_58872_needit_employees");
        employeeRecord.get(this.getParameter('sysparm_employeeRecord'));
        return employeeRecord.position.getDisplayValue() + '';
    },
    getEmployeeNames: function() {
        var employeeRecord = new GlideRecord("x_58872_needit_employees");
        employeeRecord.get(this.getParameter('sysparm_employeeRecord'));
		var fist_name = employeeRecord.first_name + '';
		var last_name = employeeRecord.last_name + '';
        return fist_name + ';' + last_name;
    },
    type: 'EmployeeInformation'
});
